    @android.webkit.JavascriptInterface
    public void actionFromJsWebRefresh() {
        Log.i(TAG, "Refreshing webpage");
        runOnUiThread(new Runnable() {
            @Override
            public void run() {
                MainActivity.this.loadWebView();
